function [SC_raw,FC_raw,TS_raw,sub_info,sub_info_text,n_sub] = data2cellAll(infoPath)%从dataPath读取数据，返回SC结构矩阵，FC序列和相关矩阵，被试人口学信息，被试3*7数目%infoPath = 'F:Code\coupling\data\ConfigRaw\sub_only.csv';sublist = importdata(infoPath);sub_info= sublist.data;%数字型数据，为人口学信息sub_text= sublist.textdata;%文字性数据，三项数据  Subj_fmri	Subj_t1	dirPath，其他数据表头SC_raw = cell(7,3);TS_raw = cell(7,3);FC_raw = cell(7,3);A_count = ones(7,3);%取出其他数据的表头for i = 4:size(sub_text,2)    sub_info_text{1,i-3} = sub_text{1,i};endfor i = 1:21    SC_raw{mod(i,7) +1, ceil(i/7)} = cell(1);%     FC_raw{mod(i,7) +1, ceil(i/7)} = cell(1);%     TS_raw{mod(i,7) +1, ceil(i/7)} = cell(1);endfor i = 1 : length(sub_text)-1    %第三项为文件夹路径    subdirPath = sub_text{i+1,3};    S = regexp(sub_text{i+1,3}, '\', 'split');    center = str2num(S{7}(3));    if S{8} == 'NC'        group = 1;    end    if S{8} == 'MC'        group = 2;    end    if S{8} == 'AD'        group = 3;    end    %从子文件路径中读取五种数据，此处缺乏校验    output = csv2data(subdirPath);    for j = 1:length(output)        if j <=3             SC_raw{center, group}{j,A_count(center,group)} = output{j};        elseif j == 4            FC_raw{center, group}(:,:,A_count(center,group)) = output{j};        elseif j == 5            TS_raw{center, group}(:,:,A_count(center,group)) = output{j};        end    end    A_count(center,group) =  A_count(center,group) +1;    if mod(i,100) == 0        A_count-1     endendn_sub = A_count-1 end